mapper.update:
UPDATE admins SET
password = #{password},
username = #{username}, full_name = #{fullName}, email = #{email}, phone = #{phone}, avatar = #{avatar}, last_login_at = #{lastLoginAt}, last_login_ip = #{lastLoginIp}, updated_at = #{updatedAt}, deleted_at = #{deletedAt} WHERE id = #{id}

mapper.select:
SELECT id
FROM admins


    ORDER BY ${orderBy}


    LIMIT #{limit}


    OFFSET #{offset}
